import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

function Files() {
  return (
    <div class="files_wrapper">
      <div class="item">
        1
      </div>

      <div class="item">
        2
      </div>

      <div class="item">
        3
      </div>

      <div class="item">
        4
      </div>
    </div>
  )
}

function Login_form_html(){
  return(
    <div>

    </div>
  )
}

function Login_form() {

  const [login, setLogin] = useState("");
  const [password, setPwd] = useState("");
  const [renderFiles, renderItems] = useState(false);

  const login_confirm = () => {
    const login_input = document.querySelector(".login");
    const pwd_input = document.querySelector(".pwd");

    if (login === "" || password === "") {
      if (login === "") {
        login_input.style.color = "red";
      }
      if (password === "") {
        pwd_input.style.color = "red";
      }
      return;
    }

    login_input.style.color = "black";
    pwd_input.style.color = "black";

    fetch("login", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        "login": login,
        "password": password
      })
    })

    const main_div = document.querySelector(".main");

    const newElement = document.createElement('div');
    newElement.textContent = 'Новый элемент';

    main_div.innerHTML = "";
    renderItems(true)
  }



  return (
    <div>
      {renderFiles && <Files />}
      {!renderFiles && <Login_form_html/>}
    </div>
  )
}

function Header() {
  return (
    <div class="header_wrapper">
      <div class="header">
        <div class="item">
          <p>data storage</p>
        </div>

        <div class="item">
          <a href='/about'>о проекте</a>
        </div>

        <div class="item">
          <a href='/'>profile</a>
        </div>
      </div>
    </div>
  )
}

function App() {
  return (
    <div>
      <Header />
      <div class="main">
        <Login_form />
      </div>
    </div>
  );
}

export default App;
