import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

function Files() {
  return (
    <div class="files_wrapper">
      <div class="item">
        1
      </div>

      <div class="item">
        2
      </div>

      <div class="item">
        3
      </div>

      <div class="item">
        4
      </div>
    </div>
  )
}

function Login_form() {

  const [login, setLogin] = useState("");
  const [password, setPwd] = useState("");
  const [renderFiles, renderItems] = useState(false);

  const login_confirm = () => {
    const login_input = document.querySelector(".login");
    const pwd_input = document.querySelector(".pwd");

    if (login === "" || password === "") {
      if (login === "") {
        login_input.style.color = "red";
      }
      if (password === "") {
        pwd_input.style.color = "red";
      }
      return;
    }

    login_input.style.color = "black";
    pwd_input.style.color = "black";

    fetch("login", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify({
        "login": login,
        "password": password
      })
    })

    const main_div = document.querySelector(".main");

    const newElement = document.createElement('div');
    newElement.textContent = 'Новый элемент';

    main_div.innerHTML = "";
    renderItems(true)
  }



  return (
    <div>
      {renderFiles && <Files />}
    <div className="login_wrapper">
      <h3>Вход</h3>

      <div class="inputs">
        <div class="input_wrapper">
          <p>Логин: </p>
          <input
            class="login"
            placeholder='type login here'
            type="email"
            onChange={(e) => setLogin(e.target.value)} />
        </div>

        <div class="input_wrapper">
          <p>Пароль: </p>
          <input placeholder='type password here'
            class="pwd"
            type="password"
            onChange={(e) => setPwd(e.target.value)} />
        </div>
      </div>

      <button class="submit-btn" onClick={login_confirm}>Войти</button>
    </div>
    </div>
  )
}

function Header() {
  return (
    <div class="header_wrapper">
      <div class="header">
        <div class="item">
          <p>data storage</p>
        </div>

        <div class="item">
          <a href='/about'>о проекте</a>
        </div>

        <div class="item">
          <a href='/'>profile</a>
        </div>
      </div>
    </div>
  )
}

function App() {
  return (
    <div>
      <Header />
      <div class="main">
        <Login_form />
      </div>
    </div>
  );
}

export default App;
