import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

async function Files() {
  await fetch("/getFiles", {
    method: "POST",
    headers: {
      "Content-type": "application/json"
    },
    body: JSON.stringify({
      "token": localStorage.getItem('token')
    })
  }).then((resp) =>{
    return (
      <div class="files_wrapper">
        <div class="item">
          resp[0]["name"]
        </div>
      </div>
    )
  })
}

function login_confirm() {
  const login_input = document.querySelector(".login");
  const pwd_input = document.querySelector(".pwd");

  const login = login_input.value;
  const pwd = pwd_input.value;

  if (login === "" || pwd === "") {
    if (login_input.value === "") {
      login_input.style.color = "red";
    }
    if (pwd_input.value === "") {
      pwd_input.style.color = "red";
    }
    return;
  }

  login_input.style.color = "black";
  pwd_input.style.color = "black";

  fetch("login", {
    method: "POST",
    headers: {
      "Content-type": "application/json"
    },
    body: JSON.stringify({
      "login": login,
      "password": pwd
    })
  }).then(response => {
  })
}

function Login_form() {
  const [renderFiles, renderItems] = useState(false);

  return (
    <div>
      {!renderFiles && (
        <div className="login_wrapper">
          <h3>Вход</h3>

          <div class="inputs">
            <div class="input_wrapper">
              <p>Логин: </p>
              <input
                class="login"
                placeholder='type login here'
                type="email" />
            </div>

            <div class="input_wrapper">
              <p>Пароль: </p>
              <input placeholder='type password here'
                class="pwd"
                type="password" />
            </div>
          </div>

          <button class="submit-btn" onClick={() => {
            login_confirm();
            renderItems(true);
          }}>Войти</button>
        </div>
      )}
      {renderFiles && <Files />}
    </div>
  )
}

function Header() {
  return (
    <div class="header_wrapper">
      <div class="header">
        <div class="item">
          <p>data storage</p>
        </div>

        <div class="item">
          <a href='/about'>о проекте</a>
        </div>

        <div class="item">
          <a href='/'>profile</a>
        </div>
      </div>
    </div>
  )
}

function App() {
  return (
    <div>
      <Header />
      <div class="main">
        <Login_form />
      </div>
    </div>
  );
}

export default App;
