import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

function Files() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    async function fetchFiles() {
      const response = await fetch("/getFiles", {
        method: "POST",
        headers: {
          "Content-type": "application/json"
        },
        body: JSON.stringify({
          "token": localStorage.getItem('token')
        })
      });

      if (response.ok) {
        const data = await response.json();
        setFiles(data);
      }
    }

    fetchFiles();
  }, []); // Пустой массив зависимостей гарантирует, что запрос выполнится только один раз

  return (
    <div className="files_wrapper">
      {files.map((file, index) => (
        <div key={index} className="item">
          {file.name}
        </div>
      ))}
    </div>
  );
}

async function login_confirm() {
  const login_input = document.querySelector(".login");
  const pwd_input = document.querySelector(".pwd");

  const login = login_input.value;
  const pwd = pwd_input.value;

  if (login === "" || pwd === "") {
    if (login_input.value === "") {
      login_input.style.color = "red";
    }
    if (pwd_input.value === "") {
      pwd_input.style.color = "red";
    }
    return;
  }

  login_input.style.color = "black";
  pwd_input.style.color = "black";

  response = await fetch("login", {
    method: "POST",
    headers: {
      "Content-type": "application/json"
    },
    body: JSON.stringify({
      "login": login,
      "password": pwd
    })
  })
  if ((await response.json())["result"] == "ok"){
    return true;
  }
  return false;
}

function Login_form() {
  const [renderFiles, setRenderFiles] = useState(false);

  return (
    <div>
      {!renderFiles && (
        <div className="login_wrapper">
          <h3>Вход</h3>

          <div className="inputs">
            <div className="input_wrapper">
              <p>Логин: </p>
              <input
                className="login"
                placeholder='type login here'
                type="email" />
            </div>

            <div className="input_wrapper">
              <p>Пароль: </p>
              <input placeholder='type password here'
                className="pwd"
                type="password" />
            </div>
          </div>

          <button className="submit-btn" onClick={async () =>{
            const status = await login_confirm();
            if (status){
              setRenderFiles(true);
            }
          }}>Войти</button>
        </div>
      )}
      {renderFiles && <Files />}
    </div>
  );
}

function Header() {
  return (
    <div className="header_wrapper">
      <div className="header">
        <div className="item">
          <p>data storage</p>
        </div>

        <div className="item">
          <a href='/about'>о проекте</a>
        </div>

        <div className="item">
          <a href='/'>profile</a>
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <div>
      <Header />
      <div className="main">
        <Login_form />
      </div>
    </div>
  );
}

export default App;
